import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  LineChart, Line,
  BarChart, Bar,
  XAxis, YAxis,
  Tooltip, Legend,
  ResponsiveContainer,
  CartesianGrid,
  Label
} from 'recharts';

const FinanceDashboard = () => {
  const [sales, setSales] = useState([]);
  const [incomeStatements, setIncomeStatements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const salesResponse = await axios.get('http://localhost:5000/api/sales');
        setSales(salesResponse.data);

        const incomeResponse = await axios.get('http://localhost:5000/api/income');
        setIncomeStatements(incomeResponse.data);
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const totalSales = sales.reduce((acc, sale) => acc + sale.totalAmount, 0);

  const styles = {
    container: {
      padding: '30px',
      fontFamily: 'Arial, sans-serif',
    },
    title: {
      fontSize: '40px',
      fontWeight: 'bold',
      marginBottom: '30px',
      textAlign: 'center',
      color: '#374151',
    },
    section: {
      backgroundColor: '#ffffff',
      padding: '30px',
      borderRadius: '20px',
      boxShadow: '0px 8px 20px rgba(0,0,0,0.15)',
      marginBottom: '50px',
      maxWidth: '1000px',
      margin: '0 auto',
    },
    sectionTitle: {
      fontSize: '30px',
      marginBottom: '20px',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
    },
    tableHeader: {
      backgroundColor: '#93c5fd',
      padding: '12px',
    },
    tableCell: {
      padding: '10px',
      borderBottom: '1px solid #eee',
      textAlign: 'center',
    },
    noData: {
      padding: '20px',
      textAlign: 'center',
      fontStyle: 'italic',
      color: '#777',
    },
    error: {
      color: 'red',
      textAlign: 'center',
    },
    totalSales: {
      fontSize: '24px',
      fontWeight: 'bold',
    },
    chartTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      textAlign: 'center',
      marginBottom: '15px',
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2,
    }).format(value);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Finance Dashboard</h2>
      <p style={{ textAlign: 'center', marginBottom: '30px' }}>
        Welcome, Finance team. Here you can manage financial records.
      </p>

      {loading ? (
        <p style={{ textAlign: 'center' }}>Loading financial data...</p>
      ) : error ? (
        <p style={styles.error}>{error}</p>
      ) : (
        <>
          {/* Total Sales */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Total Sales</h3>
            <p style={styles.totalSales}>${totalSales.toFixed(2)}</p>
          </div>

          {/* Sales History */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Sales History</h3>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.tableHeader}>Product</th>
                  <th style={styles.tableHeader}>Quantity Sold</th>
                  <th style={styles.tableHeader}>Total Amount</th>
                  <th style={styles.tableHeader}>Date Sold</th>
                </tr>
              </thead>
              <tbody>
                {sales.map((sale, index) => (
                  <tr key={index}>
                    <td style={styles.tableCell}>{sale.product?.name || 'Unknown'}</td>
                    <td style={styles.tableCell}>{sale.quantitySold}</td>
                    <td style={styles.tableCell}>${sale.totalAmount.toFixed(2)}</td>
                    <td style={styles.tableCell}>{new Date(sale.saleDate).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Income Statements Table */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Income Statements</h3>
            {incomeStatements.length === 0 ? (
              <p style={styles.noData}>No income statements available.</p>
            ) : (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th style={styles.tableHeader}>Month</th>
                    <th style={styles.tableHeader}>Year</th>
                    <th style={styles.tableHeader}>Total Sales</th>
                    <th style={styles.tableHeader}>Total Expenses</th>
                    <th style={styles.tableHeader}>Net Income</th>
                  </tr>
                </thead>
                <tbody>
                  {incomeStatements.map((statement) => (
                    <tr key={statement._id}>
                      <td style={styles.tableCell}>{statement.month}</td>
                      <td style={styles.tableCell}>{statement.year}</td>
                      <td style={styles.tableCell}>{formatCurrency(statement.totalSales)}</td>
                      <td style={styles.tableCell}>{formatCurrency(statement.totalExpenses)}</td>
                      <td style={styles.tableCell}>{formatCurrency(statement.netIncome)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>

          {/* Income Statement Charts */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Income Statement Charts</h3>

            {/* Line Chart */}
            <div style={styles.chartContainer}>
              <p style={styles.chartTitle}>Income Trends</p>
              {incomeStatements.length > 0 && (
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart data={incomeStatements} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month"
                      tick={{ fontSize: 12 }}
                      label={{ value: 'Month', position: 'bottom', offset: 0, style: { textAnchor: 'middle', fontSize: 14 } }} />
                    <YAxis tickFormatter={formatCurrency}
                      tick={{ fontSize: 12 }}
                      label={{ value: 'Amount ($)', angle: -90, position: 'left', style: { textAnchor: 'middle', fontSize: 14 } }} />
                    <Tooltip formatter={formatCurrency} />
                    <Legend wrapperStyle={{ fontSize: '14px' }} />
                    <Line type="monotone" dataKey="totalSales" stroke="#22c55e" strokeWidth={2} name="Total Sales" dot={false} />
                    <Line type="monotone" dataKey="totalExpenses" stroke="#ef4444" strokeWidth={2} name="Expenses" dot={false} />
                    <Line type="monotone" dataKey="netIncome" stroke="#3b82f6" strokeWidth={2} name="Net Income" dot={false} />
                  </LineChart>
                </ResponsiveContainer>
              )}
            </div>

            {/* Bar Chart */}
            <div style={styles.chartContainer}>
              <p style={styles.chartTitle}>Income Summary</p>
              {incomeStatements.length > 0 && (
                <ResponsiveContainer width="100%" height={400}>
                  <BarChart data={incomeStatements} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month"
                      tick={{ fontSize: 12 }}
                      label={{ value: 'Month', position: 'bottom', offset: 0, style: { textAnchor: 'middle', fontSize: 14 } }} />
                    <YAxis tickFormatter={formatCurrency}
                      tick={{ fontSize: 12 }}
                      label={{ value: 'Amount ($)', angle: -90, position: 'left', style: { textAnchor: 'middle', fontSize: 14 } }} />
                    <Tooltip formatter={formatCurrency} />
                    <Legend wrapperStyle={{ fontSize: '14px' }} />
                    <Bar dataKey="totalSales" fill="#22c55e" name="Total Sales" />
                    <Bar dataKey="totalExpenses" fill="#ef4444" name="Expenses" />
                    <Bar dataKey="netIncome" fill="#3b82f6" name="Net Income" />
                  </BarChart>
                </ResponsiveContainer>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default FinanceDashboard;
