import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const FinanceDashboard = () => {
  const [sales, setSales] = useState([]);
  const [incomeStatements, setIncomeStatements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const salesResponse = await axios.get('http://localhost:5000/api/sales');
        setSales(salesResponse.data);

        const incomeResponse = await axios.get('http://localhost:5000/api/income');
        setIncomeStatements(incomeResponse.data);
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Calculate total sales from all records
  const totalSales = sales.reduce((accumulator, sale) => accumulator + sale.totalAmount, 0);

  // Prepare data for Income Statement chart
  const incomeStatementLabels = incomeStatements.map((statement) => `${statement.month}-${statement.year}`);
  const netIncomeData = incomeStatements.map((statement) => statement.netIncome);
  const totalSalesData = incomeStatements.map((statement) => statement.totalSales);
  const totalExpensesData = incomeStatements.map((statement) => statement.totalExpenses);

  const incomeStatementChartData = {
    labels: incomeStatementLabels,
    datasets: [
      {
        label: 'Net Income',
        data: netIncomeData,
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
        tension: 0.4,
        pointRadius: 3,
        borderWidth: 2,
      },
      {
        label: 'Total Sales',
        data: totalSalesData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
        tension: 0.4,
        pointRadius: 3,
        borderWidth: 2,
      },
      {
        label: 'Total Expenses',
        data: totalExpensesData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
        tension: 0.4,
        pointRadius: 3,
        borderWidth: 2,
      },
    ],
  };

  const incomeStatementChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      title: {
        display: true,
        text: 'Income Statement Trends',
        font: {
          size: 18
        }
      },
      legend: {
        display: true,
        position: 'bottom',
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Month-Year',
          font: {
            size: 14
          }
        },
        grid: {
          display: false
        }
      },
      y: {
        title: {
          display: true,
          text: 'Amount ($)',
          font: {
            size: 14
          }
        },
        beginAtZero: true,
        ticks: {
          callback: function (value, index, values) {
            return '$' + value;
          }
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.1)'
        }
      }
    },
  };

  const styles = {
    container: {
      padding: '30px',
      fontFamily: 'Arial, sans-serif',
    },
    title: {
      fontSize: '40px',
      fontWeight: 'bold',
      marginBottom: '30px',
      textAlign: 'center',
      color: '#374151',
    },
    loading: {
      textAlign: 'center',
    },
    section: {
      backgroundColor: '#ffffff',
      padding: '30px',
      borderRadius: '20px',
      boxShadow: '0px 8px 20px rgba(0,0,0,0.15)',
      marginBottom: '50px',
      maxWidth: '800px',
      marginLeft: 'auto',
      marginRight: 'auto',
    },
    sectionTitle: {
      fontSize: '30px',
      marginBottom: '20px',
    },
    totalSales: {
      fontSize: '24px',
      fontWeight: 'bold',
    },
    table: {
      width: '100%',
      textAlign: 'center',
      borderCollapse: 'collapse',
    },
    tableHeader: {
      backgroundColor: '#93c5fd',
      padding: '12px',
    },
    tableCell: {
      padding: '10px',
      borderBottom: '1px solid #eee',
    },
    noData: {
      padding: '20px',
      textAlign: 'center',
      fontStyle: 'italic',
      color: '#777',
    },
    error: {
      color: 'red',
      marginBottom: '10px',
      textAlign: 'center',
    },
    chartContainer: {
      width: '100%',
      maxWidth: '800px',
      margin: '0 auto',
      height: '400px', // Fixed height for a more consistent look
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Finance Dashboard</h2>
      <p style={{ textAlign: 'center', marginBottom: '30px' }}>
        Welcome, Finance team. Here you can manage financial records.
      </p>

      {loading ? (
        <p style={styles.loading}>Loading financial data...</p>
      ) : error ? (
        <p style={styles.error}>{error}</p>
      ) : (
        <>
          {/* Total Sales Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Total Sales</h3>
            <p style={styles.totalSales}>${totalSales.toFixed(2)}</p>
          </div>

          {/* Sales History Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Sales History</h3>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.tableHeader}>Product</th>
                  <th style={styles.tableHeader}>Quantity Sold</th>
                  <th style={styles.tableHeader}>Total Amount</th>
                  <th style={styles.tableHeader}>Date Sold</th>
                </tr>
              </thead>
              <tbody>
                {sales.map((sale, index) => (
                  <tr key={index}>
                    <td style={styles.tableCell}>{sale.product?.name || 'Unknown'}</td>
                    <td style={styles.tableCell}>{sale.quantitySold}</td>
                    <td style={styles.tableCell}>${sale.totalAmount.toFixed(2)}</td>
                    <td style={styles.tableCell}>{new Date(sale.saleDate).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Income Statements Section */}
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Income Statements</h3>
            {incomeStatements.length === 0 ? (
              <p style={styles.noData}>No income statements available.</p>
            ) : (
              <>
                <div style={styles.chartContainer}>
                  <Line data={incomeStatementChartData} options={incomeStatementChartOptions} />
                </div>
                <table style={styles.table}>
                  <thead>
                    <tr>
                      <th style={styles.tableHeader}>Month</th>
                      <th style={styles.tableHeader}>Year</th>
                      <th style={styles.tableHeader}>Total Sales</th>
                      <th style={styles.tableHeader}>Total Expenses</th>
                      <th style={styles.tableHeader}>Net Income</th>
                    </tr>
                  </thead>
                  <tbody>
                    {incomeStatements.map((statement) => (
                      <tr key={statement._id}>
                        <td style={styles.tableCell}>{statement.month}</td>
                        <td style={styles.tableCell}>{statement.year}</td>
                        <td style={styles.tableCell}>{statement.totalSales}</td>
                        <td style={styles.tableCell}>{statement.totalExpenses}</td>
                        <td style={styles.tableCell}>{statement.netIncome}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default FinanceDashboard;
